building_array = building.split('')

floor = 0

for i in building_array
	if i == '('
	    floor +=1
	elsif i == ')'
	    floor -=1
	end 
end
puts floor

# part 2
floor = 0
building_array.each_with_index do |i, index|
	if i == '('
	    floor +=1
	elsif i == ')'
	    floor -=1
	end 
	if floor == -1
	    puts index+1
	    break
	end
end
